+}
+
+.container-customleft {
+    padding-left: calc((100vw - 1200px)/2);
+}
+
+h3,
+.titleh3,
+h2,
+.titleh2 {
+    text-align: center;
+    color: #fff;
+    font-size: 48px;
+    font-weight: 700;
+    line-height: 130%;
