+@mixin gradient-text1 {
+    background: linear-gradient(125.34deg, #CDFF7C 0%, #8CFFAC 10.63%, #93FFEB 51%, white 50%);
+    -webkit-background-clip: text;
+    -webkit-text-fill-color: transparent;
+    background-size: 250% 100%;
+    -webkit-transition: background-position 0.5s ease;
+    transition: background-position 0.5s ease;
+    background-position: 0 0;
+
+    &:hover {
+        background-position: 100% 0;
+    }
+}
+
+@mixin bodytext3 {
+    font-size: 16px;
+    font-weight: 500;
+    line-height: 150%;
+    color: #fff;
+}
+
+@mixin bodytext4 {
+    font-size: 14px;
+    font-weight: 500;
+    line-height: 150%;
+    color: #fff;
+}
+
+.section-subtitle {
+    span {
+        @include gradient-subtitle;
+        display: inline-block;
+    }
+
+    text-align: center;
+    text-transform: uppercase;
+    line-height: 130%;
+}
+
+.borderbtn {
+    display: inline-flex;
+    align-items: center;
+    justify-content: center;
+    text-align: center;
+    border: 1px solid;
+    border-image-slice: 1;
+    border-width: 1px;
+    border-image-source: linear-gradient(77.09deg, #cdff7c 28.29%, #8cffac 87.22%);
+    font-size: 18px;
+    font-weight: 700;
+    line-height: 130%;
+    color: var(--lime);
+    width: 100%;
+    min-height: 67px;
+    max-width: 285px;
+    padding: 5px 10px;
+    background: var(--black100);
+    transition: 0.3s;
+
+    &:hover {
+        background: var(--lime);
+        color: vaR(--black100);
+        box-shadow: 0px 8px 20px rgba(67, 253, 130, 0.25), 0px 26px 50px #2f463a;
+    }
+}
+
+
+// -----------------
+.white-arrbtn {
+    background: #fff;
+    border-radius: 8px;
+    min-width: 200px;
+    display: inline-flex;
+    align-items: center;
+    justify-content: center;
+    height: 47px;
+    color: var(--dark);
+    font-size: 18px;
+    font-weight: 700;
+    font-family: "PT Sans", sans-serif;
+
+    img {
+        margin-left: 4px;
+        margin-top: -1px;
+    }
+}
+
+.greenborder-btn {
+    height: 44px;
+    background: transparent;
+    border: 1px solid var(--lime);
+    border-radius: 8px;
+    min-width: 250px;
+    display: inline-flex;
+    align-items: center;
+    justify-content: center;
+    padding: 3px 10px;
+    color: #fff;
+    font-size: 16px;
+    font-family: "PT Sans", sans-serif;
+    font-weight: 700;
+
+
+    img {
+        margin-left: 4px;
+        margin-top: -1px;
+    }
+}
+
+.text-center {
+    text-align: center;
+}
+
+.section-subtitle {
+    text-transform: uppercase;
+    color: var(--dark-grey);
+    font-family: "PT Sans", sans-serif;
+    font-size: 20px;
+    line-height: 130%;
+    margin-bottom: 16px;
+}
+
+.section-group.white {
+    background: #fff;
+}
+
+
+@media(max-width:1399.8px) {
+    .section-subtitle {
+        font-size: 14px;
+    }
+    
+    .titleh2, .titleh3, h2, h3 {
+        font-size: 40px;
+    }
+}
+
+@media(max-width:575.8px) {
+    .titleh2, .titleh3, h2, h3 {
+        font-size: 32px;
+    }
+
+    .section-subtitle {
+        font-size: 12px;
+    }
+
+    .borderbtn {
+        min-height: 56px;
+        max-width: 100%;
+    }
+}
