+    justify-content: center;
+    text-align: center;
+    border: 1px solid;
+    border-image-slice: 1;
+    border-width: 1px;
+    border-image-source: linear-gradient(77.09deg, #cdff7c 28.29%, #8cffac 87.22%);
+    font-size: 18px;
+    font-weight: 700;
+    line-height: 130%;
+    color: var(--lime);
+    width: 100%;
+    min-height: 67px;
+    max-width: 285px;
+    padding: 5px 10px;
+    background: var(--black100);
+    transition: 0.3s;
+
+    &:hover {
+        background: var(--lime);
+        color: vaR(--black100);
