// hero section

.herosection {
    position: relative;
    height: 100vh;
    min-height: 700px;

    .container {
        position: relative;
        z-index: 2;
    }
}

.herosection__bg {
    position: absolute;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    object-fit: cover;
    object-position: 100% 0;
}

.herosection__wrapper {
    min-height: 700px;
    padding: 175px 0 140px;
    height: 100vh;
    display: flex;
    align-items: center;
    // flex-direction: column;
    // justify-content: space-between;
}

.herosection__topinfo {
    margin-top: -150px;
    width: 100%;

    h1 {
        color: #fff;
        line-height: 125%;
        font-size: 64px;
        font-weight: 700;
        margin-bottom: 24px;

        i {
            font-style: normal;
            display: inline-block;
            position: relative;

            &::after {
                content: '';
                display: inline-block;
                width: calc(100% + 50px);
                height: 60px;
                background: url('../images/main/titlebgel.png') no-repeat center / 100%;
                position: absolute;
                left: -20px;
                bottom: -30px;

            }
        }
    }

    p {
        @include bodytext3;
        margin-bottom: 40px;
    }

    a {
        background: #fff;
        display: inline-flex;
        align-items: center;
        justify-content: center;
        text-align: center;
        min-height: 48px;
        border: 1px solid #fff;
        padding: 4px 32px;
        color: #000;
        font-weight: 700;
        line-height: 130%;
        transition: 0.3s;

        &:hover {
            background: var(--lime);
            border-color: var(--lime);
            box-shadow: 0px 8px 20px rgba(67, 253, 130, 0.25), 0px 26px 50px #2f463a;
        }
    }
}

.herosection__bottinfo {
    position: absolute;
    left: 0;
    bottom: 40px;
    width: 100%;

    p {
        margin: 0;
        color: #828282;
        font-weight: 500;
        line-height: 135%;

        a {
            @include gradient-text1;
        }
    }

    ul {
        display: flex;
        align-items: center;
        justify-content: flex-end;

        li {
            margin-right: 48px;

            img {
                max-height: 100px;
            }

            &:last-child {
                margin-right: 0;
            }
        }
    }

}

// Featured clients

.clientsslider {
    padding: 40px 0;

    p {
        font-weight: 700;
        line-height: 135%;
        margin: 0;
        color: #fff;
        font-size: 18px;
    }

    .brandsslider {
        .swiper-slide {
            width: auto;

            .brandblock {
                height: 40px;
                display: flex;
                align-items: center;
                padding: 0 50px;
            }

            img {
                max-height: 40px;
            }
        }
    }

    .swiper-wrapper {
        transition-timing-function: linear;
    }
}

// expertise

.expertise {
    padding: 100px 0;
    position: relative;
    background: radial-gradient(50% 50% at 50% 50%, rgba(#0F0F10, 0.8) 50%, rgba(19, 19, 20, 0) 100%);

    .section-subtitle {
        margin-bottom: 80px;

        span {
            @include gradient-subtitle2;
        }
    }

    .container {
        position: relative;
        z-index: 2;
    }

    .col {
        transition: 0.5s;
        animation-timing-function: ease-in-out;
    }

    .col.active {
        width: 100%;
        max-width: 50%;
        flex: 0 0 50%;

        .imgwrap {
            .title {
                transition-delay: 0.1s;
            }
        }
    }
}

.expertise__box {
    height: 310px;
    border-radius: 36px;
    border: 1px solid rgba(#E1FF8C, 0.1);
    transition: 0.3s;
    position: relative;
    padding: 56px 30px 20px 30px;
    background: var(--black100);
    overflow: hidden;
    transition: 0.3s;

    .imgwrap {
        display: flex;
        flex-wrap: wrap;
        align-items: flex-start;

        .title {
            transform: translateX(calc(100% + 70px));
            animation-timing-function: ease-in-out;
            transition: 0.5s;
            position: relative;
            text-align: left;
            width: 100%;
            max-width: calc(100% - 88px);
            flex: 0 0 calc(100% - 88px);
            padding-left: 24px;
            font-size: 24px;
            line-height: 140%;
            display: -webkit-box;
            -webkit-line-clamp: 3;
            -webkit-box-orient: vertical;
            overflow: hidden;
        }
    }

    .imgwrap img {
        position: relative;
        width: 100%;
        max-width: 88px;
        flex: 0 0 88px;
        height: 88px;
        left: 50%;
        margin-left: -44px;
        transition: 0.4s;
        margin-bottom: 24px;
    }

    .title {
        text-align: center;
        font-weight: 700;
        font-size: 24px;
        line-height: 135%;
        margin: 0;
        transition: 0.5s;

        &.v1 {
            background: linear-gradient(125.34deg, #CDFFCF 30%, #A6E9FF 50%);
            -webkit-background-clip: text;
            -webkit-text-fill-color: transparent;
        }

        &.v2 {
            background: linear-gradient(125.34deg, #FEC0D7 30%, #C7C6FD 50%);
            -webkit-background-clip: text;
            -webkit-text-fill-color: transparent;
        }

        &.v3 {
            background: linear-gradient(125.34deg, #C6C8FD 30%, #9FE3F8 50%);
            -webkit-background-clip: text;
            -webkit-text-fill-color: transparent;
        }
    }

    .descr {
        position: relative;
        overflow: hidden;
        transition: 0.3s;

        .title {
            display: -webkit-box;
            -webkit-line-clamp: 2;
            -webkit-box-orient: vertical;
            overflow: hidden;
            position: absolute;
            width: 100%;
            left: 0;

        }

        p:not(.title) {
            transition: 0.3s;
            transition-delay: 0s;
            color: #EDEFEE;
            @include bodytext4;
            transform: translateY(200px);
            margin: 0;
        }
    }

}

.col.active {
    .expertise__box {
        padding: 56px 56px 20px 56px;

        img {
            left: 0;
            margin-left: 0;
        }

        &>.title {
            transform: translateX(-400px);
        }
    }

    .imgwrap {
        .title {
            transform: translateX(0);
            font-size: 32px;
        }
    }

    .descr {
        transform: translateY(0);

        .title {
            // left: -100%;
            opacity: 0;
            // transition-delay: 0.3s;
            transform: translateX(100%);
        }

        p {
            transform: translateY(0);
        }
    }

    p:not(.title) {
        transition-delay: 0.5s;
    }

}

