// Clients’ success stories
.projectssection {
    padding: 120px 0 110px;

    h3 {
        margin-bottom: 80px;
    }

    .explore {
        color: var(--lime);
        font-size: 18px;
        line-height: 130%;
        font-weight: 700;
        display: inline-block;
        padding-right: 36px;
        position: relative;
        transition: 0.3s;

        &::after {
            content: '';
            display: inline-block;
            width: 24px;
            height: 24px;
            position: absolute;
            right: 0;
            top: 50%;
            transform: translateY(-50%);
            background: url('../images/general/button-arrow.svg') no-repeat center / 100%;
        }

        &::before {
            content: '';
            display: block;
            height: 1px;
            background: var(--lime);
            position: absolute;
            left: 0;
            bottom: -4px;
            width: 0;
            transition: 0.3s;
        }

        &:hover {
            &::before {
                width: 100%;
            }
        }
    }

    .projectsbtns .swiper-wrapper {
        -webkit-transition-timing-function: linear;
        transition-timing-function: linear;
    }

    .projectsbtns .swiper-slide {
        width: auto;
    }

}

.prbtn {
    display: inline-flex;
    align-items: center;
    margin-right: 44px;
    padding-bottom: 5px;
    cursor: pointer;

