        width: 40px;
        height: 40px;
        position: relative;
        display: inline-block;

        img {
            width: 100%;
            position: absolute;
            left: 0;
            top: 0;
            transition: 0.3s;

            &:nth-child(1) {
                opacity: 0;
            }
        }
    }

    span {
        display: inline-block;
        padding-left: 12px;
        color: var(--gray80);
        line-height: 135%;
        font-size: 18px;
        font-weight: 700;
        transition: 0.3s;
    }

    &:hover // &.active 

        {
        border-bottom: 1px solid #CDF765;

        .imgwrap {
            img {
                &:nth-child(1) {
                    opacity: 1;
                }

                &:nth-child(2) {
                    opacity: 0;
                }
            }
        }

        span {
            color: #ffff;
        }
    }

}

