// DROP US
.dropussection {
    padding: 120px 0;
}

.dropusblock {
    border-radius: 24px;
    border: 1px solid rgba(140, 255, 172, 0.2);
    background: rgba(#0F0F10, 0.9);
    box-shadow: 0 4px 11px rgba(#000, 0.4);
    padding: 40px;
}

.dropusblock__left {
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    align-items: flex-start;
    height: 100%;

    p {
        max-width: 550px;
        color: #EDEFEE;
        @include bodytext3;
    }

    .title {
        margin-bottom: 32px;
        font-size: 40px;
        font-weight: 700;
        line-height: 135%;
    }

    a {

        span {
            font-size: 32px;
            line-height: 135%;
            @include gradient-text1;
            display: inline-block;
            position: relative;

            &::after {
                content: '';
                display: block;
                width: 0;
                height: 2px;
                position: absolute;
                bottom: 0;
                left: 0;
                background: #a9ff97;
                transition: 0.3s;
            }
        }

        img {
            max-width: 48px;
            vertical-align: bottom;
            margin-bottom: -2px;
            transition: 0.3s;
        }

        &:hover {
            img {
                animation: arrowmove 2s infinite;
            }

            span {
                &::after {
                    width: 100%;
                }
            }
        }
    }
}

@keyframes arrowmove {
    0% {
        transform: translate(0, 0);
    }

    50% {
        transform: translate(5px, -5px);
    }

    100% {
        transform: translate(0, 0);
    }
}

// faq

.faqsection {
    padding: 120px 0 120px;
    background: rgba(#0F0F10, 0.9);

    .section-subtitle {
        text-align: left;
    }

    h3 {
        text-align: left;
        font-size: 40px;
    }
}

.faqrow {
    position: relative;
    margin-bottom: 16px;
    border: 0.5px solid;
    border-image-slice: 1;
    border-width: 1px;
    border-image-source: linear-gradient(180deg, rgba(219, 255, 116, 0.1) 0%, rgba(255, 255, 255, 0) 100%);

    &::after {
        content: "";
        position: absolute;
        left: 0;
        top: 0;
        width: 100%;
        height: 100%;
        background: url(../images/general/noise.png), #09090A;
        opacity: 0.8;
    }
}

.faq__header {
    position: relative;
    z-index: 2;
    display: flex;
    align-items: center;
    padding: 10px 32px;
    min-height: 88px;
    cursor: pointer;

    p {
        margin: 0;
        padding-right: 50px;
        color: #fff;
        line-height: 130%;
        font-weight: 700;
        font-size: 18px;
        width: 100%;
    }

    .faqstatusicon {
        position: absolute;
        right: 22px;
        top: 50%;
        transform: translateY(-50%);
        width: 56px;
        height: 56px;
        display: flex;
        align-items: center;
        justify-content: center;

        span {
            display: inline-block;
            width: 35px;
            height: 1.5px;
            background: linear-gradient(-180deg, #D9FF6F 40%, #B1FFDE 60%);
            position: absolute;
            transition: 0.3s;

            &:nth-child(2) {
                transform: rotate(-90deg);
            }
        }
    }

    &.active {
        .faqstatusicon {
            span {
                &:nth-child(2) {
                    transform: translate(0);
                }
            }
        }
    }
}

.faqbody {
    position: relative;
    z-index: 2;
    padding: 8px 22px 32px 32px;
    display: none;

