commits.content:
.serv-testimonials {
    margin-bottom: 163px;
    position: relative;
    z-index: 2;

    .slider-wrapper {
        margin-top: 60px;
        padding-bottom: 30px;
    }

    .swiper-slide {
        transition: all 200ms linear;
        transform: scale(0.8);
        filter: blur(4px);

        &.swiper-slide-active {
            transform: scale(1);
            filter: blur(0);
        }
    }

    .testim__slidebox {
        border-radius: 24px;
        background: #141414;
        padding: 40px 40px 40px 103px;

        .q {
            width: 43px;
            margin-left: -63px;
            margin-bottom: -10px;
        }

        .slwrap {
            display: flex;
            flex-direction: column;
            justify-content: space-between;
            min-height: 160px;

            &>p {
                font-family: "PT Sans", sans-serif;
                font-size: 16px;
                color: var(--dark-grey);
                margin-bottom: 40px;
                // display: -webkit-box;
                // -webkit-line-clamp: 3;
                // -webkit-box-orient: vertical;
                // overflow: hidden;
            }
        }

        .testim__slidebox-bottsect {
            display: flex;
            flex-wrap: wrap;
            align-items: center;
            justify-content: space-between;
        }

        .author {
            display: flex;
            flex-wrap: wrap;
            align-items: center;
            width: 100%;
            max-width: calc(100% - 165px);
            flex: 0 0 calc(100% - 165px);

            .imgwrap {
                width: 100%;
                max-width: 48px;
                flex: 0 0 48px;
                border-radius: 50%;
                overflow: hidden;

                img {
                    width: 100%;
                    height: 100%;
                    object-fit: cover;
                    object-position: 50% 50%;
                    vertical-align: top;
                }
            }

            .authordescr {
                padding-left: 24px;

                .name {
                    color: #fff;
                    font-size: 16px;
                    line-height: 135%;
                    font-weight: 500;
                    font-family: 'PowerGrotesk';
                    margin-bottom: 6px;

                    img {
                        width: 18px;
                        vertical-align: middle;
                        margin-top: -3px;
                    }
                }

                .prof {
                    margin: 0;
                    color: var(--dark-grey);
                    font-family: "PT Sans", sans-serif;
                    font-size: 14px;
                    line-height: 140%;
                }
            }
        }

        .testim__rating {
            width: 100%;
            max-width: 165px;
            flex: 0 0 165;
            display: flex;
            align-items: center;

            span {
                font-family: "PT Sans", sans-serif;
                font-size: 16px;
                color: #fff;
                width: 100%;
                max-width: 21px;
                flex: 0 0 21px;
            }

            .stars {
                padding-left: 8px;
                width: 100%;
                max-width: calc(100% - 21px);
                flex: 0 0 calc(100% - 21px);

                img {
                    width: 23px;
                    vertical-align: top;
                }
            }

        }
    }

    .greenborder-btn {
        margin-top: 20px;
    }

}
