+++ 
@@ -0,0 +1,625 @@
+footer.footer {
+    background: #fff;
+    padding: 64px 120px 40px;
+}
+
+.footer__wrapper {
+    display: flex;
+    flex-wrap: wrap;
+
+    .footer__info {
+        width: 100%;
+        max-width: 283px;
+        flex: 0 0 283px;
+    }
+
+    .footer__rightsect {
+        width: 100%;
+        max-width: calc(100% - 283px);
+        flex: 0 0 calc(100% - 283px);
+        display: flex;
+        flex-wrap: wrap;
+        align-items: flex-start;
+    }
+
+    .footer__lists {
+        width: 100%;
+        max-width: calc(100% - 282px);
+        flex: 0 0 calc(100% - 282px);
+    }
+
+    .footer__listsmain {
+        width: 100%;
+        max-width: 282px;
+        flex: 0 0 282px;
+    }
+}
+
+.footer__info {
+    &>.title {
+        font-size: 36px;
+        font-weight: 700;
+        line-height: 114%;
+        color: #000;
+        margin-bottom: 16px;
+    }
+
+    .mainemail {
+        display: inline-block;
+        margin-bottom: 32px;
+        font-size: 16px;
+        line-height: 130%;
+        color: #000;
+    }
+
+    .footerbtn {
+        font-size: 16px;
+        color: #0F0F10;
+        font-weight: 700;
+        line-height: 120%;
+        border: 1px solid #C9FA3D;
+        display: inline-block;
+        -webkit-transition: 0.3s;
+        transition: 0.3s;
+        padding: 12px 21px;
+        background: #C9FA3D;
+        max-width: 196px;
+        width: 100%;
+        text-align: center;
+        margin-bottom: 32px;
+
+        svg {
+            vertical-align: middle;
+            margin-top: -3px;
+        }
+    }
+}
+
+.footer__country {
+    margin-bottom: 32px;
+
+    .title {
+        color: #000;
+        font-size: 14px;
+        font-weight: 700;
+        line-height: 130%;
+        margin-bottom: 8px;
+
+        img {
+            display: inline-block;
+            vertical-align: middle;
+            margin-top: -3px;
+            width: 18px;
+        }
+    }
+
+    p {
+        font-size: 14px;
+        color: #000;
+        line-height: 145%;
+    }
+}
+
+.footer__imglist {
+    list-style: none;
+    margin: 60px 0 0 0;
+    padding: 0;
+
+    li {
+        margin-bottom: 32px;
+
+        img {
+            max-width: 100%;
+            width: 130px;
+            
+        }
+    }
+}
+
+.footer__lists {
+    display: flex;
+    flex-wrap: wrap;
+    align-items: flex-start;
+
+    .footer__list {
+        width: 100%;
+        padding-right: 16px;
+        margin-bottom: 0;
+        max-width: 33.333%;
+        flex: 0 0 33.333%;
+    }
+}
+
+.footer__listsmain .footer__list {
+    max-width: 100%;
+    flex: 0 0 100%;
+    padding-right: 16px;
+    margin-bottom: 0;
+}
+
+.footer__list {
+    min-height: 344px;
+}
+
+.footer__lists,
+.footer__listsmain {
+
+    .footer__list {
+
+        .title {
+            margin-bottom: 16px;
+            font-size: 18px;
+            font-weight: 700;
+            line-height: 145%;
+        }
+
+        ul {
+            list-style: none;
+            margin: 0;
+            padding: 0;
+
+            li {
+                margin-bottom: 16px;
+
+                a {
+                    font-size: 14px;
+                    line-height: 145%;
+                    color: #000;
+
+                    &:hover {
+                        text-decoration: underline;
+                        text-underline-offset: 3px;
+                    }
+                }
+            }
+
+            &.footer__list-main {
+                &>li {
+
+                    &:last-child {
+                        margin-bottom: 0;
+                    }
+
+                    &>a,
+                    &>span {
+                        font-size: 16px;
+                        font-weight: 600;
+                        color: #000;
+                    }
+
+                    &.footer__dropdown {
+
+                        a,
+                        span {
+                            cursor: pointer;
+                            display: inline-block;
+
+                            svg {
+                                display: inline-block;
+                                vertical-align: middle;
+                                margin-top: -4px;
+                            }
+                        }
+
+                        ul {
+                            padding: 12px 0 0 0;
+
+                            li {
+                                margin-bottom: 12px;
+
+                                &:last-child {
+                                    margin-bottom: 0;
+                                }
+
+                                a {
+                                    font-size: 14px;
+                                    color: #000;
+                                }
+                            }
+                        }
+                    }
+                }
+            }
+
+            .fire {
+                width: 13px;
+                margin-top: -15px;
+                vertical-align: middle;
+                display: inline-block;
+
+            }
+
+            .new {
+                font-style: italic;
+                background: linear-gradient(180deg, #6CEE71, #2BACD6);
+                -webkit-background-clip: text;
+                -webkit-text-fill-color: transparent;
+                font-size: 12px;
+                font-weight: 500;
+                margin-top: -15px;
+                vertical-align: middle;
+                display: inline-block;
+                padding-right: 2px;
+            }
+        }
+    }
+}
+
+.footer__showmore {
+    display: inline-block;
+    font-size: 13px;
+    line-height: 130%;
+    color: #000;
+    margin-top: 5px;
+    cursor: pointer;
+    opacity: 0.7;
+    transition: 0.3s;
+
+    i {
+        font-style: normal;
+    }
+
+    svg {
+        width: 12px;
+        display: inline-block;
+        vertical-align: middle;
+    }
+
+    &.rotate {
+        svg {
+            transform: rotate(-180deg);
+        }
+    }
+
+    &:hover {
+        opacity: 1;
+    }
+}
+
+.footer__bottomsect {
+    display: flex;
+    flex-wrap: wrap;
+    justify-content: space-between;
+    margin-top: 80px;
+    align-items: center;
+
+    ul {
+        list-style: none;
+        margin: 0;
+        padding: 0;
+
+        li {
+            display: inline-block;
+            vertical-align: middle;
+            position: relative;
+            margin-right: 28px;
+
+            &::after {
+                content: '';
+                display: inline-block;
+                border-radius: 50%;
+                width: 6px;
+                height: 6px;
+                background: grey;
+                position: absolute;
+                top: 50%;
+                transform: translateY(-50%);
+                right: -20px;
+            }
+
+            &:last-child {
+                margin-right: 0;
+
+                &::after {
+                    display: none;
+                }
+            }
+
+            img {
+                vertical-align: top;
+                width: 30px;
+            }
+        }
+    }
+
+    p {
+        font-size: 16px;
+        color: #000;
+        line-height: 114%;
+        margin: 0;
+
+        span {
+            color: red;
+        }
+    }
+
+    &>a {
+        text-decoration: underline;
+        color: #000;
+        font-size: 16px;
+        line-height: 114%;
+    }
+}
+
+.footer__lists .footer__list ul li {
+    display: none;
+
+    &:nth-child(-n+6) {
+        display: block;
+    }
+}
+
+.footer__lists .footer__list ul.showall li {
+    display: block !important;
+}
+
+.footer__dropdown {
+
+    &>ul {
+        display: none;
+    }
+
+    span {
+
+        &:hover {
+            text-decoration: underline;
+        }
+
+        svg {
+            transition: 0.3s;
+        }
+
+        &.active {
+            svg {
+                transform: rotate(-180deg);
+            }
+        }
+    }
+}
+
+.footerteamsl .slick-slide img {
+    width: 100%;
+}
+
+.footer__sliderwrap {
+    background: #fff;
+}
+
+.footer__list {
+    margin-bottom: 20px !important;
+}
+
+.footer__showmore {
+    display: none;
+}
+
+.footer__country p a {
+    color: #000;
+}
+
+@media(max-width:1600.8px) {
+
+    .footer__lists .footer__list ul.footer__list-main>li>a {
+        font-size: 15px;
+    }
+}
+
+@media(max-width:1440.8px) {
+    footer.footer {
+        padding: 64px;
+    }
+
+    .footer__lists .footer__list {
+        max-width: 50%;
+        flex: 0 0 50%;
+    }
+
+    .footer__wrapper .footer__info,
+    .footer__wrapper .footer__listsmain {
+        width: 100%;
+        max-width: 288px;
+        flex: 0 0 288px;
+    }
+
+    .footer__wrapper .footer__rightsect {
+        max-width: calc(100% - 288px);
+        flex: 0 0 calc(100% - 288px);
+    }
+
+    .footer__wrapper .footer__lists {
+        width: 100%;
+        max-width: calc(100% - 288px);
+        flex: 0 0 calc(100% - 288px);
+    }
+
+}
+
+@media(max-width:1399.8px) {
+    footer.footer {
+        padding: 60px 40px 40px 40px;
+    }
+}
+
+@media(max-width:1280.8px) {
+
+    .footer__wrapper .footer__info,
+    .footer__wrapper .footer__listsmain {
+        width: 100%;
+        max-width: 270px;
+        flex: 0 0 270px;
+    }
+
+    .footer__wrapper .footer__lists {
+        max-width: calc(100% - 270px);
+        flex: 0 0 calc(100% - 270px);
+    }
+
+    .footer__wrapper .footer__rightsect {
+        max-width: calc(100% - 270px);
+        flex: 0 0 calc(100% - 270px);
+    }
+}
+
+@media(max-width:1199.8px) {
+    .footer__info>.title {
+        font-size: 32px;
+    }
+
+    .footer__country p {
+        max-width: 240px;
+    }
+
+    .footer__lists .footer__list ul li {
+        display: none;
+
+        &:nth-child(-n+10) {
+            display: none;
+        }
+
+        &:nth-child(-n+6) {
+            display: block;
+        }
+    }
+
+    .footer__wrapper .footer__info,
+    .footer__wrapper .footer__listsmain {
+        width: 100%;
+        max-width: 230px;
+        flex: 0 0 230px;
+    }
+
+    .footer__wrapper .footer__lists {
+        max-width: calc(100% - 230px);
+        flex: 0 0 calc(100% - 230px);
+    }
+
+    .footer__wrapper .footer__rightsect {
+        max-width: calc(100% - 230px);
+        flex: 0 0 calc(100% - 230px);
+    }
+}
+
+@media(max-width:991.8px) {
+
+    .footer__wrapper .footer__listsmain {
+        max-width: 100%;
+        flex: 0 0 100%;
+        display: flex;
+        flex-wrap: wrap;
+
+        .footer__list {
+            width: 100%;
+            max-width: 50%;
+            flex: 0 0 50%;
+        }
+    }
+
+    .footer__listsmain .footer__list {
+        min-height: initial;
+    }
+
+    .footer__wrapper .footer__lists {
+        max-width: 100%;
+        flex: 0 0 100%;
+    }
+
+    .footer__list {
+        margin-bottom: 40px !important;
+    }
+}
+
+@media(max-width:767.8px) {
+
+    .footer__wrapper .footer__info,
+    .footer__wrapper .footer__lists {
+        max-width: 100%;
+        flex: 0 0 100%;
+    }
+
+    .footer__info .mainemail {
+        margin-bottom: 20px;
+    }
+
+    .footer__country p {
+        max-width: 100%;
+    }
+
+    .footer__wrapper .footer__info {
+        margin-bottom: 50px;
+    }
+
+    .footer__bottomsect {
+        text-align: center;
+        justify-content: center;
+        margin-top: 0;
+
+        ul,
+        p {
+            max-width: 100%;
+            flex: 0 0 100%;
+        }
+
+        ul {
+            order: 1;
+            margin-bottom: 15px;
+        }
+
+        a {
+            order: 2;
+        }
+
+        p {
+            order: 3;
+            margin-top: 15px;
+        }
+    }
+
+    .footer__wrapper .footer__rightsect {
+        max-width: 100%;
+        flex: 0 0 100%;
+    }
+
+}
+
+@media(max-width:575.8px) {
+    footer.footer {
+        padding: 30px 16px 20px 16px;
+    }
+
+    .footer__info>.title,
+    .footer__info .mainemail {
+        margin-bottom: 10px;
+    }
+
+    .footer__info .footerbtn {
+        margin-bottom: 30px;
+    }
+
+    .footer__country {
+        margin-bottom: 20px;
+    }
+
+    .footer__imglist {
+        margin-top: 40px;
+    }
+
+    .footer__lists .footer__list {
+        max-width: 100% !important;
+        flex: 0 0 100% !important;
+        min-height: initial;
+    }
+
+    .footer__showmore {
+        margin-top: 0;
+    }
+
+    .footer__lists .footer__list ul {
+        margin-bottom: 20px;
+    }
+
+    .footer__wrapper .footer__listsmain .footer__list {
+        max-width: 100%;
+        flex: 0 0 100%;
+    }
+
+}
