+footer.footer {
+    background: #fff;
+    padding: 64px 120px 40px;
+    font-family: "PT Sans", sans-serif;
+}
+
+.footer__topf {
+    margin-bottom: 40px;
+
+    .title {
+        font-size: 40px;
+        font-family: 'PowerGrotesk';
+        font-weight: 500;
+        margin-bottom: 16px;
+    }
+
+    .footer__email {
+        margin-bottom: 32px;
+
+        a {
+            color: var(--dark);
+            font-size: 20px;
+            line-height: 150%;
+        }
+    }
+
+    .footer__lt {
+        width: 100%;
+        max-width: 260px;
+        display: flex;
+        align-items: center;
+        justify-content: center;
+        color: var(--dark);
+        font-size: 16px;
+        font-weight: 700;
+        border-radius: 8px;
+        background: var(--lime);
+        height: 44px;
+
+        svg {
+            margin-left: 4px;
+        }
+    }
+}
+
+.footer__contaddr {
+    display: flex;
+    flex-wrap: wrap;
+    padding-top: 11px;
+
+    .footer__contaddrbox {
+        width: 100%;
+        max-width: 33.333%;
+        flex: 0 0 33.333%;
+
+        .fcountry {
+            color: var(--dark);
+            font-size: 14px;
+            line-height: 140%;
+            font-weight: 700;
+            margin-bottom: 8px;
+
+            img {
+                vertical-align: middle;
+            }
+        }
+
+        .descr,
+        .descr a {
+            color: var(--dark);
