+header.header {
+    position: fixed;
+    left: 0;
+    top: 0;
+    width: 100%;
+    z-index: 15;
+    height: 94px;
+    display: flex;
+    align-items: center;
+    transition: 0.3s;
+    border-bottom: 1px solid transparent;
+
+    &.white {
+        background: #fff;
+    }
+
+    &.scroll {
+        background: rgba(0, 0, 0, 0.9);
+        height: 80px;
+    }
+
+    &.black {
+        background: rgba(0, 0, 0, 1);
+    }
+}
+
+.header__logo {
+    transition: 0.3s;
+
+    img {
+        width: 100%;
+        max-width: 172px;
+    }
+}
+
+.header__nav {
+    &>ul {
+        text-align: center;
+
+        &>li {
+            display: inline-block;
+            vertical-align: middle;
+            margin-right: 40px;
+
+            &:last-child {
+                margin-right: 0;
+            }
+
+            &>a,
+            &>span {
+                font-weight: 700;
+                color: #fff;
+                cursor: pointer;
+                background: linear-gradient(to right, white, white 50%, #8CFFAC 55%, #93FFEB);
+                -webkit-background-clip: text;
+                -webkit-text-fill-color: transparent;
+                background-size: 220% 100%;
+                transition: background-position 0.5s ease;
+
+                &:hover {
+                    background-position: 100%;
+                }
+            }
+
+            &.dropdown {
+                &>span {
+                    position: relative;
+                    padding-right: 20px;
+
+                    &::after {
+                        content: '';
+                        display: inline-block;
+                        width: 16px;
+                        height: 16px;
+                        background: url('../images/general/menu-arrow.svg') no-repeat center / 100%;
+                        position: absolute;
+                        right: 0;
+                        top: 2px;
+                        transition: 0.3s;
+                    }
+
+                    &.rotate {
+                        &::after {
+                            transform: rotate(-180deg);
+                        }
+                    }
+                }
+            }
+        }
+    }
+}
+
+.header__contactlink {
+    display: inline-flex;
+    align-items: center;
+    justify-content: center;
+    min-height: 46px;
+    width: 100%;
+    max-width: 147px;
+    border: 1px solid var(--lime);
+    color: var(--lime);
+    font-weight: 700;
+    line-height: 120%;
+    transition: 0.3s;
+
+    img {
+        margin-left: 6px;
+        transition: 0.3s;
+    }
+
+    &:hover {
+        background: var(--lime);
+        color: var(--black100);
+        border-color: var(--lime);
+
+        img {
+            filter: brightness(0);
+        }
+    }
+}
+
+// active header
+header.header {
+
+    &.active,
+    &.white {
+        background: #fff;
+        border-bottom: 1px solid var(--lime);
+
+        .header__logo,
+        .header__contactlink img,
+        .header__nav>ul>li.dropdown>span:after {
+            filter: brightness(0);
+        }
+
+        nav>ul>li>a,
+        nav>ul>li>span {
+            // background: linear-gradient(to right, #000, #000 50%, #8CFFAC 55%, #93FFEB);
