@@ -0,0 +1,395 @@
+// hero bg animation
+:root {
+    --color-bg1: rgb(15, 15, 16);
+    --color-bg2: rgb(15, 15, 16);
+    --color1: 147, 255, 235;
+    --color2: 147, 255, 235;
+    --color3: 205, 247, 101;
+    --color4: 147, 255, 235;
+    --color5: 205, 247, 101;
+    --color-interactive: 205, 247, 101;
+    --circle-size: 80%;
+    --blending: hard-light;
+}
+
+@keyframes moveInCircle {
+    0% {
+        transform: rotate(0deg);
+    }
+
+    50% {
+        transform: rotate(180deg);
+    }
+
+    100% {
+        transform: rotate(360deg);
+    }
+}
+
+@keyframes moveVertical {
+    0% {
+        transform: translateY(-50%);
+    }
+
+    50% {
+        transform: translateY(50%);
+    }
+
+    100% {
+        transform: translateY(-50%);
+    }
+}
+
+@keyframes moveHorizontal {
+    0% {
+        transform: translateX(-50%) translateY(-10%);
+    }
+
+    50% {
+        transform: translateX(50%) translateY(10%);
+    }
+
+    100% {
+        transform: translateX(-50%) translateY(-10%);
+    }
+}
+
+.gradient-bg {
+    width: 100vw;
+    height: calc(100vh + 20px);
+    position: absolute;
+    overflow: hidden;
+    background: linear-gradient(40deg, var(--color-bg1), var(--color-bg2));
+    top: 0;
+    right: 0;
+    opacity: 0.7;
+
+    .svgBlur {
+        display: none;
+    }
+
+    .noiseBg {
+        position: absolute;
+        width: 100%;
+        height: 100%;
+        top: 0;
+        left: 0;
+        z-index: 1;
+        mix-blend-mode: soft-light;
+        opacity: 0.3;
+    }
+
+    .gradients-container {
+        filter: url(#goo) blur(40px);
+        width: 100%;
+        height: 100%;
+    }
+
+    .g1 {
+        position: absolute;
+        background: radial-gradient(circle at center, rgba(var(--color1), 0.8) 0, rgba(var(--color1), 0) 50%) no-repeat;
+        mix-blend-mode: var(--blending);
+
+        width: var(--circle-size);
+        height: var(--circle-size);
+        top: calc(50% - var(--circle-size) / 2);
+        left: calc(50% - var(--circle-size) / 2);
+
+        transform-origin: center center;
+        animation: moveVertical 30s ease infinite;
+
+        opacity: 0.6;
+    }
+
+    .g2 {
+        position: absolute;
+        background: radial-gradient(circle at center, rgba(var(--color2), 0.8) 0, rgba(var(--color2), 0) 50%) no-repeat;
+        mix-blend-mode: var(--blending);
+
+        width: var(--circle-size);
+        height: var(--circle-size);
+        top: calc(50% - var(--circle-size) / 2);
+        left: calc(50% - var(--circle-size) / 2);
+
+        transform-origin: calc(50% - 400px);
+        animation: moveInCircle 20s reverse infinite;
+
+        opacity: 0.7;
+    }
+
+    .g3 {
+        position: absolute;
+        background: radial-gradient(circle at center, rgba(var(--color3), 0.8) 0, rgba(var(--color3), 0) 50%) no-repeat;
+        mix-blend-mode: var(--blending);
+        width: var(--circle-size);
+        height: var(--circle-size);
+        top: calc(50% - var(--circle-size) / 2 + 200px);
+        left: calc(50% - var(--circle-size) / 2 - 500px);
+        transform-origin: calc(50% + 400px);
+        animation: moveInCircle 40s linear infinite;
+        opacity: 0.6;
+    }
+
+    .g4 {
+        position: absolute;
+        background: radial-gradient(circle at center, rgba(var(--color4), 0.8) 0, rgba(var(--color4), 0) 50%) no-repeat;
+        mix-blend-mode: var(--blending);
+        width: var(--circle-size);
+        height: var(--circle-size);
+        top: calc(50% - var(--circle-size) / 2);
+        left: calc(50% - var(--circle-size) / 2);
+        transform-origin: calc(50% - 200px);
+        animation: moveHorizontal 40s ease infinite;
+        opacity: 0.6;
+    }
+
+    .g5 {
+        position: absolute;
+        background: radial-gradient(circle at center, rgba(var(--color5), 0.8) 0, rgba(var(--color5), 0) 50%) no-repeat;
+        mix-blend-mode: var(--blending);
+        width: calc(var(--circle-size) * 2);
+        height: calc(var(--circle-size) * 2);
+        top: calc(50% - var(--circle-size));
+        left: calc(50% - var(--circle-size));
+        transform-origin: calc(50% - 800px) calc(50% + 200px);
+        animation: moveInCircle 20s ease infinite;
+        opacity: 0.6;
+    }
+
+    .interactive {
+        position: absolute;
+        background: radial-gradient(circle at center, rgba(var(--color-interactive), 0.8) 0, rgba(var(--color-interactive), 0) 50%) no-repeat;
+        mix-blend-mode: var(--blending);
+        width: 100%;
+        height: 100%;
+        top: -50%;
+        left: -50%;
+        opacity: 0.7;
+    }
+}
+
+// hero bg animation - END
+
+
+@mixin body1 {
+    font-family: "PT Sans", sans-serif;
+    font-size: 20px;
+    font-weight: 400;
+    line-height: 150%;
+}
+
+@mixin body4 {
+    font-size: 14px;
+    font-weight: 400;
+    line-height: 140%;
+}
+
+
+// hero sect
+.services__hero {
+    height: calc(100vh + 20px);
+    background: var(--black100);
+    overflow: hidden;
+    position: relative;
+    padding: 60px 0;
+    display: flex;
+    align-items: center;
+
+    .container {
+        position: relative;
+        z-index: 1;
+    }
+
+    h1 {
+        font-weight: 500;
+        color: #fff;
+        font-size: 60px;
+        line-height: 110%;
+        font-family: 'PowerGrotesk';
+        margin-bottom: 20px;
+
+        span {
+            display: inline-block;
+            line-height: 110%;
+            background: linear-gradient(125.34deg, #CDFF7C 10%, #8CFFAC 70%);
+            -webkit-background-clip: text;
+            -webkit-text-fill-color: transparent;
+        }
+    }
+
+    @mixin aaa {
+        font-family: "PT Sans", sans-serif;
+        font-size: 20px;
+        font-weight: 400;
+        line-height: 150%;
+    }
+
+    p {
+        max-width: 473px;
+        margin-bottom: 40px;
+        color: #CFD1CC;
+        @include aaa;
+    }
+}
+
+.services-heroblockwrap {
+    display: flex;
+    justify-content: flex-end;
+    padding-right: 30px;
+}
+
+.services-heroblock {
+    width: 453px;
+    height: 316px;
+    position: relative;
+
+    img {
+        border-radius: 16px;
+        border: 4px solid #242424;
+    }
+}
+
+.servheroinfo {
+    border-radius: 16px;
+    width: 140px;
+    padding: 16px 20px;
+    position: absolute;
+    transform-style: preserve-3d;
+
+    .servheroinfo-content {
+        transform-style: preserve-3d;
+
+        span {
+            transform: translateZ(50px);
+        }
+
+        p {
+            transform: translateZ(25px);
+        }
+    }
+
+    &::after {
+        content: '';
+        display: block;
+        position: absolute;
+        left: 0;
+        top: 0;
+        background: rgba(#141414, 0.4);
+        width: 100%;
+        height: 100%;
+        backdrop-filter: blur(50px);
+        border-radius: 16px;
+    }
+
+    span {
+        font-family: 'PowerGrotesk';
+        color: var(--lime);
+        display: block;
+        font-size: 40px;
+        font-weight: 500;
+    }
+
+    p {
+        margin: 0;
+        color: var(--lime);
+        font-size: 14px;
+        line-height: 140%;
+    }
+
+    &:nth-child(2) {
+        left: -76px;
+        top: -49px;
+    }
+
+    &:nth-child(3) {
+        right: -47px;
+        top: -79px;
+
+        span,
+        p {
+            color: #fff;
+        }
+    }
+
+    &:nth-child(4) {
+        bottom: -83px;
+        right: -26px;
+
+        span {
+            color: #0F0F10;
+        }
+
+        p {
+            color: #161616;
+        }
+
+        &::after {
+            backdrop-filter: initial;
+            background: var(--lime);
+        }
+    }
+
+    &:nth-child(5) {
+        width: 123px;
+        bottom: -73px;
+        left: -136px;
+    }
+
+}
+
+// Success stories
+.success-stories {
+    position: relative;
+    z-index: 1;
+    padding: 120px 0;
+    background: #fff;
+    border-radius: 20px;
+    margin-top: -20px;
+}
+
+.successstory__left {
+
+    h2 {
+        text-align: left;
+        margin: 0;
+        color: var(--dark);
+        font-size: 48px;
+        line-height: 120%;
+        font-family: 'PowerGrotesk';
+        font-weight: 500;
+    }
+
+    p {
+        font-family: "PT Sans", sans-serif;
+        margin: 0;
+        color: #858B89;
+        font-size: 20px;
+    }
+}
+
+.successstory__right {
+    border-radius: 20px;
+    background: var(--lime);
+    padding: 30px 26px 26px 30px;
+    display: flex;
+    justify-content: space-between;
+
+    .textbox {
+        width: 135px;
+
+        p {
+            font-family: "PT Sans", sans-serif;
+            margin: 0;
+            color: var(--dark);
+            @include body4;
+        }
+
+        .title {
+            font-family: 'PowerGrotesk';
+            font-size: 24px;
+            font-weight: 500;
+            line-height: 130%;
+            margin-bottom: 3px;
+        }
+    }
+}
