+// hero bg animation
+:root {
+    --color-bg1: rgb(15, 15, 16);
+    --color-bg2: rgb(15, 15, 16);
+    --color1: 147, 255, 235;
+    --color2: 147, 255, 235;
+    --color3: 205, 247, 101;
+    --color4: 147, 255, 235;
+    --color5: 205, 247, 101;
+    --color-interactive: 205, 247, 101;
+    --circle-size: 80%;
+    --blending: hard-light;
+}
+
+@keyframes moveInCircle {
+    0% {
+        transform: rotate(0deg);
+    }
+
+    50% {
+        transform: rotate(180deg);
+    }
+
+    100% {
+        transform: rotate(360deg);
+    }
+}
+
+@keyframes moveVertical {
+    0% {
+        transform: translateY(-50%);
+    }
+
+    50% {
+        transform: translateY(50%);
+    }
+
+    100% {
+        transform: translateY(-50%);
+    }
+}
+
+@keyframes moveHorizontal {
+    0% {
+        transform: translateX(-50%) translateY(-10%);
+    }
+
+    50% {
+        transform: translateX(50%) translateY(10%);
+    }
+
+    100% {
+        transform: translateX(-50%) translateY(-10%);
+    }
+}
