+
+// hero sect
+.services__hero {
+    height: calc(100vh + 20px);
+    background: var(--black100);
+    overflow: hidden;
+    position: relative;
+    padding: 60px 0;
+    display: flex;
+    align-items: center;
+
+    .container {
+        position: relative;
+        z-index: 1;
+    }
+
+    h1 {
+        font-weight: 500;
+        color: #fff;
+        font-size: 60px;
+        line-height: 110%;
+        font-family: 'PowerGrotesk';
+        margin-bottom: 20px;
+
+        span {
+            display: inline-block;
+            line-height: 110%;
+            background: linear-gradient(125.34deg, #CDFF7C 10%, #8CFFAC 70%);
+            -webkit-background-clip: text;
+            -webkit-text-fill-color: transparent;
+        }
+    }
+
+    @mixin aaa {
+        font-family: "PT Sans", sans-serif;
+        font-size: 20px;
+        font-weight: 400;
+        line-height: 150%;
+    }
+
+    p {
+        max-width: 473px;
+        margin-bottom: 40px;
+        color: #CFD1CC;
+        @include aaa;
+    }
+}
+
+.services-heroblockwrap {
+    display: flex;
+    justify-content: flex-end;
+    padding-right: 30px;
+}
+
+.services-heroblock {
+    width: 453px;
+    height: 316px;
+    position: relative;
+
+    img {
+        border-radius: 16px;
+        border: 4px solid #242424;
+    }
+}
+
+.servheroinfo {
+    border-radius: 16px;
+    width: 140px;
+    padding: 16px 20px;
+    position: absolute;
+    transform-style: preserve-3d;
+
+    .servheroinfo-content {
+        transform-style: preserve-3d;
+
+        span {
+            transform: translateZ(50px);
+        }
+
+        p {
+            transform: translateZ(25px);
+        }
+    }
+
+    &::after {
+        content: '';
+        display: block;
+        position: absolute;
+        left: 0;
+        top: 0;
+        background: rgba(#141414, 0.4);
+        width: 100%;
+        height: 100%;
+        backdrop-filter: blur(50px);
+        border-radius: 16px;
+    }
+
+    span {
+        font-family: 'PowerGrotesk';
