+.services-heroblockwrap {
+    display: flex;
+    justify-content: flex-end;
+    padding-right: 30px;
+}
+
+.services-heroblock {
+    width: 453px;
+    height: 316px;
+    position: relative;
+
+    img {
+        border-radius: 16px;
+        border: 4px solid #242424;
+    }
+}
+
+.servheroinfo {
+    border-radius: 16px;
+    width: 140px;
+    padding: 16px 20px;
+    position: absolute;
+    transform-style: preserve-3d;
+
+    .servheroinfo-content {
+        transform-style: preserve-3d;
+
+        span {
+            transform: translateZ(50px);
+        }
+
+        p {
+            transform: translateZ(25px);
+        }
+    }
+
+    &::after {
+        content: '';
+        display: block;
+        position: absolute;
+        left: 0;
+        top: 0;
+        background: rgba(#141414, 0.4);
+        width: 100%;
+        height: 100%;
+        backdrop-filter: blur(50px);
+        border-radius: 16px;
+    }
+
+    span {
+        font-family: 'PowerGrotesk';
