+        font-weight: 500;
+    }
+
+    p {
+        margin: 0;
+        color: var(--lime);
+        font-size: 14px;
+        line-height: 140%;
+    }
+
+    &:nth-child(2) {
+        left: -76px;
+        top: -49px;
+    }
+
+    &:nth-child(3) {
+        right: -47px;
+        top: -79px;
+
+        span,
+        p {
+            color: #fff;
+        }
+    }
+
+    &:nth-child(4) {
+        bottom: -83px;
