+
+        span {
+            color: #0F0F10;
+        }
+
+        p {
+            color: #161616;
+        }
+
+        &::after {
+            backdrop-filter: initial;
+            background: var(--lime);
+        }
+    }
+
+    &:nth-child(5) {
+        width: 123px;
+        bottom: -73px;
+        left: -136px;
+    }
+
+}
