+// service-img1
+
+
+:root {
+    --card-top-offset: 30px;
+    --numcards: 4;
+    --card-margin: 30px;
+}
+
+#cards {
+    display: grid;
+    grid-template-columns: 1fr;
+    grid-template-rows: repeat(var(--numcards), var(--card-height));
+    gap: var(--card-margin);
+    margin-top: 60px;
+}
+
+#card-1 {
