+
+// hero sect
+.services__hero {
+    height: calc(100vh + 20px);
+    background: var(--black100);
+    overflow: hidden;
+    position: relative;
+    padding: 60px 0;
+    display: flex;
+    align-items: center;
+
+    .container {
+        position: relative;
+        z-index: 1;
