+        if (window.innerWidth >= 576) {
+            if (!init) {
+                init = true;
+                swiper3 = new Swiper(".devprocslider", {
+                    spaceBetween: 20,
+                    loop: false,
+                    speed: 1000,
+                    breakpoints: {
+                        576: {
+                            slidesPerView: 3,
+                        },
+                        992: {
+                            slidesPerView: 5,
+                        },
+                    },
+                });
+            }
+        } else if (init) {
+            swiper3.destroy();
+            init = false;
+        }
+    }
+    swiperCard();
+    window.addEventListener("resize", swiperCard);
+
+    // Our team expertise
+    const swiper4 = new Swiper('.teamexpslider', {
+        slidesPerView: 1.8,
+        spaceBetween: 0,
+        loop: true,
+        speed: 1000,
+        breakpoints: {
+            575: {
+                slidesPerView: 3,
+            },
+            768: {
+                slidesPerView: 5,
+            },
+            1200: {
+                slidesPerView: 6,
+            },
+        },
+    });
+
+    // SELECT 2
+
+    if ($('.selectwrap__img').length) {
+        // $('.selectwrap__img .styledselect').select2({
+        //     // placeholder: "Project Type*",
