    // Featured clients slider

    var swiper = new Swiper(".brandsslider", {
        slidesPerView: "auto",
        spaceBetween: 20,
        loop: true,
        speed: 6000,
        allowTouchMove: false,
        autoplay: {
            delay: 1,
            disableOnInteraction: false,
        },
    });

    // expertise hover box
    $('.expertise .col').hover(function () {
        $('.expertise .col').removeClass('active');
        $(this).addClass('active');
    });





    // Projects
