    const swiperpr = new Swiper('.projbtns', {
        slidesPerView: "auto",
        spaceBetween: 20,
        loop: true,
        speed: 2000,
        autoplay: {
            delay: 2500,
            disableOnInteraction: false,
        },
    });
    
    const swiper1 = new Swiper('.mySwiper2', {
        loop: true,
        spaceBetween: 150,
        speed: 2000,
        effect: "fade",
        autoplay: {
            delay: 2500,
            disableOnInteraction: false,
        },
        thumbs: {
            swiper: swiperpr,
        },
        breakpoints: {
            992: {
                autoHeight: true,
            },
            1200: {
                autoHeight: false,
            },
        },
    });
    
    $('.projbtns .swiper-slide').hover(function () {
        swiperpr.autoplay.stop();
        swiper1.autoplay.stop();
    }, function () {
        swiperpr.autoplay.start();
